// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/// @title Charity platform for transparent donations and disbursements
/// @notice Admins can create campaigns and disburse funds; supporters can donate, comment and like
contract Charity {
    address public owner;

    struct Campaign {
        uint256 id;
        address creator; // admin who created
        string title;
        string description;
        string media; // uri for image/video
        string location; // area/district/province
        uint256 targetAmount;
        address payable campaignWallet; // optional external wallet
        uint256 collected;
        uint256 createdAt;
        bool active;
    }

    struct Donation {
        address donor;
        uint256 amount;
        uint256 timestamp;
        uint256 blockNumber;
    }

    struct Disbursement {
        address recipient;
        uint256 amount;
        uint256 timestamp;
        uint256 blockNumber;
    }

    struct Comment {
        address commenter;
        string text;
        uint256 timestamp;
        bool isAnonymous;
    }

    uint256 public nextCampaignId;

    mapping(uint256 => Campaign) public campaigns;
    mapping(uint256 => Donation[]) private donations;
    mapping(uint256 => Disbursement[]) private disbursements;
    mapping(uint256 => Comment[]) private comments;

    // supporters list and quick lookup
    mapping(uint256 => address[]) private supportersList;
    mapping(uint256 => mapping(address => bool)) private hasSupported;

    // likes tracking
    mapping(uint256 => mapping(address => bool)) public liked;
    mapping(uint256 => uint256) public likesCount;

    // admins
    mapping(address => bool) public isAdmin;

    event CampaignCreated(uint256 indexed id, address indexed creator);
    event DonationReceived(uint256 indexed campaignId, address indexed donor, uint256 amount);
    event Disbursed(uint256 indexed campaignId, address indexed recipient, uint256 amount);
    event CommentAdded(uint256 indexed campaignId, address indexed commenter, string text);
    event Liked(uint256 indexed campaignId, address indexed liker);

    modifier onlyOwner() {
        require(msg.sender == owner, "only owner");
        _;
    }

    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "only admin");
        _;
    }

    constructor() {
        owner = msg.sender;
        isAdmin[msg.sender] = true;
        nextCampaignId = 1;
    }

    // owner can add/remove admins
    function setAdmin(address account, bool allowed) external onlyOwner {
        isAdmin[account] = allowed;
    }

    /// @notice Admin creates a campaign. campaignWallet can be zero â€” then funds are held in contract
    function createCampaign(
        string calldata title,
        string calldata description,
        string calldata media,
        string calldata location,
        uint256 targetAmount,
        address payable campaignWallet
    ) external onlyAdmin returns (uint256) {
        uint256 id = nextCampaignId++;
        campaigns[id] = Campaign({
            id: id,
            creator: msg.sender,
            title: title,
            description: description,
            media: media,
            location: location,
            targetAmount: targetAmount,
            campaignWallet: campaignWallet,
            collected: 0,
            createdAt: block.timestamp,
            active: true
        });

        emit CampaignCreated(id, msg.sender);
        return id;
    }

    /// @notice Donate to a campaign. If campaignWallet is set, funds are forwarded immediately there.
    function donate(uint256 campaignId) external payable {
        require(msg.value > 0, "donation must be > 0");
        Campaign storage c = campaigns[campaignId];
        require(c.id != 0, "campaign not found");
        require(c.active, "campaign not active");

        c.collected += msg.value;

        donations[campaignId].push(Donation({
            donor: msg.sender,
            amount: msg.value,
            timestamp: block.timestamp,
            blockNumber: block.number
        }));

        if (!hasSupported[campaignId][msg.sender]) {
            hasSupported[campaignId][msg.sender] = true;
            supportersList[campaignId].push(msg.sender);
        }

        // If campaign has external wallet configured, forward funds immediately
        if (c.campaignWallet != address(0)) {
            (bool ok, ) = c.campaignWallet.call{value: msg.value}("");
            require(ok, "forward failed");
        }

        emit DonationReceived(campaignId, msg.sender, msg.value);
    }

    /// @notice Admin disburses funds from contract-held balance for a campaign
    function disburseFromContract(uint256 campaignId, address payable recipient, uint256 amount) external onlyAdmin {
        Campaign storage c = campaigns[campaignId];
        require(c.id != 0, "campaign not found");
        require(amount > 0, "amount must be > 0");

        uint256 available = address(this).balance;
        require(available >= amount, "insufficient contract balance");

        (bool ok, ) = recipient.call{value: amount}("");
        require(ok, "transfer failed");

        disbursements[campaignId].push(Disbursement({
            recipient: recipient,
            amount: amount,
            timestamp: block.timestamp,
            blockNumber: block.number
        }));

        emit Disbursed(campaignId, recipient, amount);
    }

    /// @notice Admin can mark campaign inactive (e.g., finished)
    function setCampaignActive(uint256 campaignId, bool active) external onlyAdmin {
        campaigns[campaignId].active = active;
    }

    // ---------- View helpers ----------
    function getTotalFunds(uint256 campaignId) external view returns (uint256) {
        return campaigns[campaignId].collected;
    }

    function getDonationsCount(uint256 campaignId) external view returns (uint256) {
        return donations[campaignId].length;
    }

    function getDonation(uint256 campaignId, uint256 index) external view returns (Donation memory) {
        return donations[campaignId][index];
    }

    function getSupporters(uint256 campaignId) external view returns (address[] memory) {
        return supportersList[campaignId];
    }

    function getSupportersCount(uint256 campaignId) external view returns (uint256) {
        return supportersList[campaignId].length;
    }

    function getDisbursementsCount(uint256 campaignId) external view returns (uint256) {
        return disbursements[campaignId].length;
    }

    function getDisbursement(uint256 campaignId, uint256 index) external view returns (Disbursement memory) {
        return disbursements[campaignId][index];
    }

    function getCommentsCount(uint256 campaignId) external view returns (uint256) {
        return comments[campaignId].length;
    }

    function getComment(uint256 campaignId, uint256 index) external view returns (Comment memory) {
        return comments[campaignId][index];
    }

    // ---------- Comments & Likes ----------
    function addComment(uint256 campaignId, string calldata text, bool anon) external {
        require(campaigns[campaignId].id != 0, "campaign not found");
        comments[campaignId].push(Comment({
            commenter: msg.sender,
            text: text,
            timestamp: block.timestamp,
            isAnonymous: anon
        }));

        emit CommentAdded(campaignId, msg.sender, text);
    }

    function like(uint256 campaignId) external {
        require(campaigns[campaignId].id != 0, "campaign not found");
        if (!liked[campaignId][msg.sender]) {
            liked[campaignId][msg.sender] = true;
            likesCount[campaignId]++;
            emit Liked(campaignId, msg.sender);
        }
    }

    function unlike(uint256 campaignId) external {
        require(liked[campaignId][msg.sender], "not liked");
        liked[campaignId][msg.sender] = false;
        likesCount[campaignId]--;
    }

    // ---------- Utility ----------
    /// @notice Receive direct donations without specifying campaign - funds go to contract
    receive() external payable {}

    fallback() external payable {}
}
